    function hasArgument ($parameterName)
        {
        return isset($_REQUEST["$parameterName"]);
        }

    function getArgument ($parameterName)
        {
        return isset($_REQUEST["$parameterName"]) ? $_REQUEST["$parameterName"] : "";
        }

    function existsArgumentWithValue ($parameterName, $parameterValue)
        {
        /* Check whether there is an argument <parameterName> that has value
           <parameterValue>.
           There may be any number of arguments with name <parameterName> !
        */
        $query  = explode('&', $_SERVER['QUERY_STRING']);

        foreach( $query as $param )
            {
            list($name, $value) = explode('=', $param);
            if($parameterName == urldecode($name) && $parameterValue == urldecode($value))
                return true;
            }
        return false;
        }

    function tempFileName($suff) /* TODO Use this to create temporary files, if needed. */
        {
        global $dodelete;
        global $tobedeleted;
        $tmpno = tempnam('/tmp', $suff);
        if($dodelete)
            $tobedeleted[$tmpno] = true;
        return $tmpno;
        }

    function requestFile($requestParm) // e.g. "IfacettokF"
        {
        logit("requestFile({$requestParm})");

        ob_start();
        var_dump($_REQUEST);
        $dump = ob_get_clean();
        logit("_REQUEST:$dump");

        if(isset($_REQUEST[$requestParm]))
            {
            $urlbase = isset($_REQUEST["base"]) ? $_REQUEST["base"] : "http://localhost/toolsdata/";

            $item = $_REQUEST[$requestParm];
            $url = $urlbase . urlencode($item);
            logit("requestParm:$requestParm");
            logit("urlbase:$urlbase");
            logit("item:$item");
            logit("url[$url]");

            $handle = fopen($url, "r");
            if($handle == false)
                {
                logit("Cannot open url[$url]");
                return "";
                }
            else
                {
                $tempfilename = tempFileName("espeak_{$requestParm}_");
                $temp_fh = fopen($tempfilename, 'w');
                if($temp_fh == false)
                    {
                    fclose($handle);
                    logit("handle closed. Cannot open $tempfilename");
                    return "";
                    }
                else
                    {
                    while (!feof($handle))
                        {
                        $read = fread($handle, 8192);
                        fwrite($temp_fh, $read);
                        }
                    fclose($temp_fh);
                    fclose($handle);
                    return $tempfilename;
                    }
                }
            }
        logit("empty");
        return "";
        }

    function php_fix_raw_query()
        {
        logit(" php_fix_raw_query");
        $post = '';

        // Try globals array
        if (!$post && isset($_GLOBALS) && isset($_GLOBALS["HTTP_RAW_POST_DATA"]))
            $post = $_GLOBALS["HTTP_RAW_POST_DATA"];
        // Try globals variable
        if (!$post && isset($HTTP_RAW_POST_DATA))
            $post = $HTTP_RAW_POST_DATA;
        // Try stream
        if (!$post)
            {
            if (!function_exists('file_get_contents'))
                {
                $fp = fopen("php://input", "r");
                if ($fp)
                    {
                    $post = '';

                    while (!feof($fp))
                        $post = fread($fp, 1024);

                    fclose($fp);
                    }
                }
            else
                {
                $post = "" . file_get_contents("php://input");
                }
            }
        $raw = !empty($_SERVER['QUERY_STRING']) ? sprintf('%s&%s', $_SERVER['QUERY_STRING'], $post) : $post;

        logit("raw=".$raw);

        $arr = array();
        $pairs = explode('&', $raw);
        foreach($pairs as $i)
            {
            if(!empty($i))
                {
                list($name, $value) = explode('=', $i, 2);
                if (isset($arr[$name]) )
                    {
                    if (is_array($arr[$name]) )
                        {
                        $arr[$name] = array_merge($arr[$name], array($value));
                        }
                    else
                        {
                        $arr[$name] = array($arr[$name], $value);
                        }
                    }
                else
                    {
                    $arr[$name] = $value;
                    }
                }
            }
        $_REQUEST = $arr;
        # optionally return result array
        return $arr;
        }


    function findInputFile($key,$parms)
        {
        logit("key $key");
        if(array_key_exists($key , $parms))
            {
            $input = $parms[$key];
            if(is_array($input))
                {
                foreach($input as $value)
                    {
                    $ret = findInputFile($key . $value, $parms);
                    if($ret != '')
                        return $ret;
                    }
                }
            else
                {
                logit("InputIs $input");
                if($input == 'F')
                    return $key . 'F';
                if($input != '')
                    return findInputFile($key . $input, $parms);
                }
            }
        return 'F';
        }


