class SystemExit extends Exception {}
try {
    function hasArgument ($parameterName)
        {
        return isset($_REQUEST["$parameterName"]);
        }

    function getArgument ($parameterName)
        {
        return isset($_REQUEST["$parameterName"]) ? $_REQUEST["$parameterName"] : "";
        }

    function existsArgumentWithValue ($parameterName, $parameterValue)
        {
        /* Check whether there is an argument <parameterName> that has value
           <parameterValue>.
           There may be any number of arguments with name <parameterName> !
        */
        $query  = explode('&', $_SERVER['QUERY_STRING']);

        foreach( $query as $param )
            {
            list($name, $value) = explode('=', $param);
            if($parameterName == urldecode($name) && $parameterValue == urldecode($value))
                return true;
            }
        return false;
        }

    function tempFileName($suff) /* TODO Use this to create temporary files, if needed. */
        {
        global $dodelete;
        global $tobedeleted;
        $tmpno = tempnam('/tmp', $suff);
        if($dodelete)
            $tobedeleted[$tmpno] = true;
        return $tmpno;
        }

    function requestFile($requestParm) // e.g. "IfacettokF"
        {
        logit("requestFile({$requestParm})");

        if(isset($_REQUEST[$requestParm]))
            {
            $urlbase = isset($_REQUEST["base"]) ? $_REQUEST["base"] : "http://localhost/toolsdata/";

            $item = $_REQUEST[$requestParm];
            $url = $urlbase . urlencode($item);
            logit("requestParm:$requestParm");
            logit("urlbase:$urlbase");
            logit("item:$item");
            logit("url[$url]");

            $handle = fopen($url, "r");
            if($handle == false)
                {
                logit("Cannot open url[$url]");
                return "";
                }
            else
                {
                $tempfilename = tempFileName("CSTNPr_{$requestParm}_");
                $temp_fh = fopen($tempfilename, 'w');
                if($temp_fh == false)
                    {
                    fclose($handle);
                    logit("handle closed. Cannot open $tempfilename");
                    return "";
                    }
                else
                    {
                    while (!feof($handle))
                        {
                        $read = fread($handle, 8192);
                        fwrite($temp_fh, $read);
                        }
                    fclose($temp_fh);
                    fclose($handle);
                    return $tempfilename;
                    }
                }
            }
        logit("empty");
        return "";
        }

    function twocol($Ifacettok,$Ifacetseg,$Ifacetpos,$Ifacetlem)
        {
        global $mode;
        logit("twocol($Ifacettok,$Ifacetseg,$Ifacetpos,$Ifacetlem)");

        $col2file = tempFileName("twocol-results");
        if($mode == 'dry')
        {
            scrip("../bin/bracmat 'get\$\"twocol.bra\"' \$col2 $Ifacettok $Ifacetseg $Ifacetpos $Ifacetlem");
        }
        else
        {
            $command = "../bin/bracmat 'get\$\"twocol.bra\"'" . ' ' . $col2file . ' ' . $Ifacettok . ' ' . $Ifacetseg . ' ' . $Ifacetpos . ' ' . $Ifacetlem;

            logit($command);

            if(($cmd = popen($command, "r")) == NULL)
            {
                throw new SystemExit(); // instead of exit()
            }

            while($read = fgets($cmd))
            {
            }

            pclose($cmd);
        }
        return $col2file;
        }


    function NPgenkender($filename)
        {
        global $mode;
        $tmpo = tempFileName("np-genkender-raw");
        if($mode == 'dry')
        {
            scrip("  CASS/bin/cass -g ../texton-linguistic-resources/da/np_genkender/UTF8/web_np_pp_gram.fsc -o \$CSTNPrfile $filename");
        }
        else
        {
            $command = 'CASS/bin/cass -g ../texton-linguistic-resources/da/np_genkender/UTF8/web_np_pp_gram.fsc -o '. $tmpo . ' ' . $filename;
            $command = trim($command);
            logit("$command");

            logit("$tmpo");

            if(($cmd = popen($command, "r")) == NULL)
                exit(1);

            while($read = fgets($cmd))
            {
            }

            pclose($cmd);
            return $tmpo;
        }
        }

